import React, { useEffect, useState, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [sensorValue, setSensorValue] = useState(null);
  const [history, setHistory] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [theme, setTheme] = useState('dark');
  const intervalRef = useRef();

  // Fetch latest sensor value and history
  const fetchData = async () => {
    try {
      const statusRes = await fetch(`${API_BASE}/status`);
      const statusData = await statusRes.json();
      setSensorValue(statusData.sensor_value);
      setLastUpdated(statusData.timestamp);

      const historyRes = await fetch(`${API_BASE}/history`);
      const historyData = await historyRes.json();
      setHistory(historyData);
    } catch (err) {
      setSensorValue(null);
    }
  };

  useEffect(() => {
    fetchData();
    intervalRef.current = setInterval(fetchData, 2000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // Prepare data for Chart.js
  const filteredHistory = history.filter(item => item.sensor_value >= 0);
  const chartData = {
    labels: filteredHistory.map(item => new Date(item.timestamp).toLocaleTimeString()),
    datasets: [
      {
        label: 'Sensor Value',
        data: filteredHistory.map(item => item.sensor_value),
        fill: true,
        borderColor: theme === 'dark' ? '#00fff7' : '#007bff',
        backgroundColor: theme === 'dark' ? 'rgba(0,255,247,0.08)' : 'rgba(0,123,255,0.08)',
        tension: 0.4,
        pointRadius: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { display: false },
    },
    scales: {
      x: {
        title: { display: true, text: 'Time', color: theme === 'dark' ? '#00fff7' : '#007bff' },
        ticks: { color: theme === 'dark' ? '#b0eaff' : '#333' },
        grid: { color: theme === 'dark' ? 'rgba(0,255,247,0.1)' : 'rgba(0,123,255,0.1)' },
      },
      y: {
        title: { display: true, text: 'Sensor Value', color: theme === 'dark' ? '#00fff7' : '#007bff' },
        ticks: { color: theme === 'dark' ? '#b0eaff' : '#333' },
        grid: { color: theme === 'dark' ? 'rgba(0,255,247,0.1)' : 'rgba(0,123,255,0.1)' },
      },
    },
  };

  const LEAK_THRESHOLD = 700;
  const BURST_THRESHOLD = 1700;

  const handleThemeToggle = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  return (
    <div className={`futuristic-bg theme-${theme}`}>
      <div className="dashboard-collage">
        <div className="dashboard-collage-top">
          <div className="collage-topleft">
            <div className="theme-toggle-container">
              <button
                className={`theme-toggle-btn ${theme === 'dark' ? 'active' : ''}`}
                onClick={handleThemeToggle}
                aria-label="Toggle dark/light theme"
              >
                <span className="theme-toggle-icon">
                  {theme === 'dark' ? 'üåô' : '‚òÄÔ∏è'}
                </span>
                <span className="theme-toggle-label">
                  {theme === 'dark' ? 'Dark' : 'Light'} Mode
                </span>
              </button>
            </div>
            <h1 className="dashboard-title">Leak Detection <span className="neon">Dashboard</span></h1>
            <div className={`sensor-card ${sensorValue !== null && sensorValue > LEAK_THRESHOLD ? 'alert' : ''}`}>
              <div className="sensor-label">Sensor Value</div>
              <div className="sensor-value">{sensorValue !== null ? sensorValue : '--'}</div>
              {sensorValue !== null && sensorValue > LEAK_THRESHOLD && (
                <div className="leak-alert neon-text">Leak Detected!</div>
              )}
              {lastUpdated && (
                <div className="last-updated">Last updated: {new Date(lastUpdated).toLocaleString()}</div>
              )}
            </div>
            {sensorValue !== null && sensorValue > BURST_THRESHOLD && (
              <div className="burst-alert">
                <h3>Burst Detected!</h3>
                <p>Sensor value exceeded burst threshold of {BURST_THRESHOLD}.</p>
              </div>
            )}
          </div>
          <div className="collage-topright">
            <div className="chart-card">
              <h2 className="chart-title">Sensor Value (Last 20)</h2>
              <Line data={chartData} options={chartOptions} />
            </div>
          </div>
        </div>
        <div className="dashboard-collage-bottom">
          <div className="collage-bottomleft">
            <div className="location-card">
              <h2 className="location-title">Leak Location</h2>
              <div className="location-placeholder">(Location info will appear here)</div>
            </div>
          </div>
          <div className="collage-bottomright">
            <div className="authority-card">
              <h2 className="authority-title">Authority Contact</h2>
              <div className="authority-placeholder">(Contact info and action will appear here)</div>
            </div>
          </div>
        </div>
        <div className="futuristic-footer">
          Designed by AI, 2024
        </div>
      </div>
    </div>
  );
}

export default App;
