import React, { useEffect, useState, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [sensorValue, setSensorValue] = useState(null);
  const [history, setHistory] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [burstBanner, setBurstBanner] = useState(false);
  const intervalRef = useRef();

  // Fetch latest sensor value and history
  const fetchData = async () => {
    try {
      const statusRes = await fetch(`${API_BASE}/status`);
      const statusData = await statusRes.json();
      setSensorValue(statusData.sensor_value);
      setLastUpdated(statusData.timestamp);

      const historyRes = await fetch(`${API_BASE}/history`);
      const historyData = await historyRes.json();
      setHistory(historyData);

      // Show burst banner if burst detected
      if (statusData.sensor_value > BURST_THRESHOLD) {
        setBurstBanner(true);
      }
    } catch (err) {
      setSensorValue(null);
    }
  };

  useEffect(() => {
    fetchData();
    intervalRef.current = setInterval(fetchData, 2000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // Prepare data for Chart.js
  const filteredHistory = history.filter(item => item.sensor_value >= 0);
  const chartData = {
    labels: filteredHistory.map(item => new Date(item.timestamp).toLocaleTimeString()),
    datasets: [
      {
        label: 'Sensor Value',
        data: filteredHistory.map(item => item.sensor_value),
        fill: true,
        borderColor: '#00fff7',
        backgroundColor: 'rgba(0,255,247,0.08)',
        tension: 0.4,
        pointRadius: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { display: false },
    },
    scales: {
      x: {
        title: { display: true, text: 'Time', color: '#00fff7' },
        ticks: { color: '#b0eaff' },
        grid: { color: 'rgba(0,255,247,0.1)' },
      },
      y: {
        title: { display: true, text: 'Sensor Value', color: '#00fff7' },
        ticks: { color: '#b0eaff' },
        grid: { color: 'rgba(0,255,247,0.1)' },
      },
    },
  };

  const LEAK_THRESHOLD = 700;
  const BURST_THRESHOLD = 1700;
  const leakDetected = sensorValue !== null && sensorValue > LEAK_THRESHOLD;
  const burstDetected = sensorValue !== null && sensorValue > BURST_THRESHOLD;

  const handleDismissBurst = () => setBurstBanner(false);

  return (
    <div className="dashboard-bg">
      {burstBanner && burstDetected && (
        <div className="burst-banner-overlay">
          <div className="burst-banner">
            <h2>Burst Detected!</h2>
            <p>Sensor value exceeded burst threshold of {BURST_THRESHOLD}.</p>
            <button className="burst-banner-dismiss" onClick={handleDismissBurst}>Dismiss</button>
          </div>
        </div>
      )}
      <header className="dashboard-header">
        <h1 className="dashboard-heading">Leak Detection Dashboard</h1>
      </header>
      <div className="dashboard-ludo-grid">
        {/* Box 1: Sensor Value */}
        <div className="ludo-card sensor-value-card">
          <div className="sensor-label">Sensor Value</div>
          <div className="sensor-value neon-cyan">{sensorValue !== null ? sensorValue : '--'}</div>
          {lastUpdated && (
            <div className="last-updated">Last updated: {new Date(lastUpdated).toLocaleString(undefined, { hour12: false })}</div>
          )}
        </div>
        {/* Box 2: Graph */}
        <div className="ludo-card graph-card">
          <h2 className="chart-title">Sensor Value (Last 20)</h2>
          <Line data={chartData} options={chartOptions} />
        </div>
        {/* Box 3: Leak Detection */}
        <div className={`ludo-card leak-detection-card${leakDetected ? ' leak-glow-red' : ''}`}>
          <h2 className="leak-title">Leak Detection</h2>
          <div className={`leak-status ${leakDetected ? 'leak-yes neon-green' : 'leak-no neon-cyan'}`}>{leakDetected ? 'Leak Detected!' : 'No Leak Detected'}</div>
          <div className="leak-location-label">Leak Location</div>
          <div className="leak-location-value">(Location info will appear here)</div>
        </div>
        {/* Box 4: Authority Contact */}
        <div className="ludo-card authority-card">
          <h2 className="authority-title">Authority Contact</h2>
          <div className="authority-placeholder">(Contact info and alert action will appear here)</div>
        </div>
      </div>
    </div>
  );
}

export default App;
