import React, { useEffect, useState, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import './App.css';

const API_BASE = 'http://localhost:5000/api';

function App() {
  const [sensorData, setSensorData] = useState({
    sensor1: null,
    sensor2: null,
    sensor3: null,
    leak_confirmed: false,
    burst_confirmed: false,
    leak_location: null,
    confidence: 0,
    correlation_score: 0,
    stability_score: 0,
    environmental_noise: false,
    active_sensors: 0
  });
  const [history, setHistory] = useState([]);
  const [lastUpdated, setLastUpdated] = useState(null);
  const [burstBanner, setBurstBanner] = useState(false);
  const intervalRef = useRef();

  // Fetch latest sensor data and history
  const fetchData = async () => {
    try {
      const statusRes = await fetch(`${API_BASE}/status`);
      const statusData = await statusRes.json();
      setSensorData(statusData);
      setLastUpdated(statusData.timestamp);

      const historyRes = await fetch(`${API_BASE}/history`);
      const historyData = await historyRes.json();
      setHistory(historyData);

      // Show burst banner if burst detected
      if (statusData.burst_confirmed) {
        setBurstBanner(true);
      }
    } catch (err) {
      console.error('Error fetching data:', err);
      setSensorData({
        sensor1: null,
        sensor2: null,
        sensor3: null,
        leak_confirmed: false,
        burst_confirmed: false,
        leak_location: null,
        confidence: 0,
        correlation_score: 0,
        stability_score: 0,
        environmental_noise: false,
        active_sensors: 0
      });
    }
  };

  useEffect(() => {
    fetchData();
    intervalRef.current = setInterval(fetchData, 2000);
    return () => clearInterval(intervalRef.current);
  }, []);

  // Prepare data for Chart.js - show all 3 sensors
  const filteredHistory = history.filter(item => item.sensor1 >= 0 && item.sensor2 >= 0 && item.sensor3 >= 0);
  const chartData = {
    labels: filteredHistory.map(item => new Date(item.timestamp).toLocaleTimeString()),
    datasets: [
      {
        label: 'Sensor 1',
        data: filteredHistory.map(item => item.sensor1),
        fill: false,
        borderColor: '#00fff7',
        backgroundColor: 'rgba(0,255,247,0.1)',
        tension: 0.4,
        pointRadius: 2,
      },
      {
        label: 'Sensor 2',
        data: filteredHistory.map(item => item.sensor2),
        fill: false,
        borderColor: '#ff6b6b',
        backgroundColor: 'rgba(255,107,107,0.1)',
        tension: 0.4,
        pointRadius: 2,
      },
      {
        label: 'Sensor 3',
        data: filteredHistory.map(item => item.sensor3),
        fill: false,
        borderColor: '#4ecdc4',
        backgroundColor: 'rgba(78,205,196,0.1)',
        tension: 0.4,
        pointRadius: 2,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { 
        display: true,
        labels: {
          color: '#00fff7',
          font: {
            size: 12
          }
        }
      },
    },
    scales: {
      x: {
        title: { display: true, text: 'Time', color: '#00fff7' },
        ticks: { color: '#b0eaff' },
        grid: { color: 'rgba(0,255,247,0.1)' },
      },
      y: {
        title: { display: true, text: 'Sensor Values', color: '#00fff7' },
        ticks: { color: '#b0eaff' },
        grid: { color: 'rgba(0,255,247,0.1)' },
      },
    },
  };

  const leakDetected = sensorData.leak_confirmed;
  const burstDetected = sensorData.burst_confirmed;

  const handleDismissBurst = () => setBurstBanner(false);

  // Calculate average sensor value
  const avgSensorValue = sensorData.sensor1 !== null && sensorData.sensor2 !== null && sensorData.sensor3 !== null 
    ? Math.round((sensorData.sensor1 + sensorData.sensor2 + sensorData.sensor3) / 3)
    : null;

  return (
    <div className="dashboard-bg">
      {burstBanner && burstDetected && (
        <div className="burst-banner-overlay">
          <div className="burst-banner">
            <h2>üö® BURST DETECTED! üö®</h2>
            <p>Multiple sensors have confirmed a burst condition.</p>
            <p>Location: {sensorData.leak_location || 'Unknown'}</p>
            <p>Confidence: {sensorData.confidence.toFixed(1)}%</p>
            <button className="burst-banner-dismiss" onClick={handleDismissBurst}>Dismiss</button>
          </div>
        </div>
      )}
      <header className="dashboard-header">
        <h1 className="dashboard-heading">Advanced Leak Detection Dashboard</h1>
      </header>
      <div className="dashboard-ludo-grid">
        {/* Box 1: Individual Sensor Values */}
        <div className="ludo-card sensor-values-card">
          <h2 className="sensor-title">Sensor Values</h2>
          <div className="sensor-grid">
            <div className="sensor-item">
              <div className="sensor-label">Sensor 1</div>
              <div className="sensor-value neon-cyan">{sensorData.sensor1 !== null ? sensorData.sensor1 : '--'}</div>
            </div>
            <div className="sensor-item">
              <div className="sensor-label">Sensor 2</div>
              <div className="sensor-value neon-red">{sensorData.sensor2 !== null ? sensorData.sensor2 : '--'}</div>
            </div>
            <div className="sensor-item">
              <div className="sensor-label">Sensor 3</div>
              <div className="sensor-value neon-green">{sensorData.sensor3 !== null ? sensorData.sensor3 : '--'}</div>
            </div>
          </div>
          <div className="avg-sensor">
            <div className="avg-label">Average</div>
            <div className="avg-value neon-yellow">{avgSensorValue !== null ? avgSensorValue : '--'}</div>
          </div>
          {lastUpdated && (
            <div className="last-updated">Last updated: {new Date(lastUpdated).toLocaleString(undefined, { hour12: false })}</div>
          )}
        </div>

        {/* Box 2: Multi-Sensor Graph */}
        <div className="ludo-card graph-card">
          <h2 className="chart-title">Multi-Sensor Monitoring (Last 20)</h2>
          <Line data={chartData} options={chartOptions} />
        </div>

        {/* Box 3: Advanced Leak Detection */}
        <div className={`ludo-card leak-detection-card${leakDetected ? ' leak-glow-red' : ''}`}>
          <h2 className="leak-title">Advanced Leak Detection</h2>
          <div className={`leak-status ${leakDetected ? 'leak-yes neon-green' : 'leak-no neon-cyan'}`}>
            {burstDetected ? 'üö® BURST DETECTED!' : leakDetected ? '‚ö†Ô∏è LEAK DETECTED!' : '‚úÖ NO LEAK DETECTED'}
          </div>
          
          <div className="leak-details">
            <div className="detail-item">
              <span className="detail-label">Location:</span>
              <span className="detail-value">{sensorData.leak_location || 'No leak detected'}</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Confidence:</span>
              <span className="detail-value">{sensorData.confidence.toFixed(1)}%</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Correlation Score:</span>
              <span className="detail-value">{sensorData.correlation_score}%</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Stability Score:</span>
              <span className="detail-value">{sensorData.stability_score}%</span>
            </div>
            <div className="detail-item">
              <span className="detail-label">Active Sensors:</span>
              <span className="detail-value">{sensorData.active_sensors}/3</span>
            </div>
            {sensorData.environmental_noise && (
              <div className="detail-item warning">
                <span className="detail-label">‚ö†Ô∏è Environmental Noise Detected</span>
              </div>
            )}
          </div>
        </div>

        {/* Box 4: System Status */}
        <div className="ludo-card system-status-card">
          <h2 className="system-title">System Status</h2>
          <div className="status-grid">
            <div className="status-item">
              <div className="status-label">Detection Status</div>
              <div className={`status-value ${sensorData.leak_confirmed ? 'status-alert' : 'status-normal'}`}>
                {sensorData.leak_confirmed ? 'ALERT' : 'NORMAL'}
              </div>
            </div>
            <div className="status-item">
              <div className="status-label">Signal Quality</div>
              <div className={`status-value ${sensorData.correlation_score > 70 ? 'status-good' : 'status-warning'}`}>
                {sensorData.correlation_score > 70 ? 'GOOD' : 'POOR'}
              </div>
            </div>
            <div className="status-item">
              <div className="status-label">Environmental</div>
              <div className={`status-value ${sensorData.environmental_noise ? 'status-warning' : 'status-good'}`}>
                {sensorData.environmental_noise ? 'NOISE' : 'CLEAN'}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
